//Connects to a local instance of the Unreal editor with the default port of 15505 configured.
            api.Connect("localhost");

            //Connects to an instance of Unreal running on the machine with IP address 10.0.0.2, port 15505, where the Unreal editor is already in Play mode
            api.Connect("10.0.0.20", 15505, 60);

// spot is set to the Vector3 value of the object position on screen
           Vector3 spot = api.GetObjectPosition("//*[@name = 'Player1']",
               CoordinateConversion.WorldToScreenPoint);

// CubeA.Transform = Vector3(1, 2, 3)
            // CubeB.Transform = Vector3(6, 5, 4)
            d = api.GetObjectDistance("//*[@name = 'CubeA']", "//*[@name = 'CubeB']");
            Console.WriteLine(d.ToString()); // prints -5.0,-3.0,-1.0

